# #Combien y a t'il de personne dans cette array ? 
def nb_hdls(handles)
    puts "il y a #{handles.length} dans ce tableau"
end



# Quel est le handle le plus court ? 
def smallest_handle(handles)
    puts "Le handle le plus court de la liste est #{handles.min {|a, b| a.length <=> b.length }}"
end




# #Combien de handle contenant 5 caractères (@ exclue) faire un if length = 5 mettre dans un array puis calculer la size d'un handle
def big_cara_handles(handles)
    puts "Il y a #{handles.count {|x| x.length == 6}} avec 5 caractères"
end




