#Trie la liste de handle par ordre alpa
def alpha_handles(handles)
    puts "la liste par ordre alpha : #{handles.sort}"
end


# #Trie la liste par taille des handles (size ou length) check diff entre size et length

def size_handles(handles)
    puts "from the mas pequeno to the biggest option 1: #{handles.sort_by {|x| x.size}}"
end
# puts "from the mas pequeno to the biggest option 2: #{handles.sort_by {|x| x.length}}"

# #QUelle est la position dans le handle de la personne @epenser
def position_handles(handles)
    puts "La personne @epenser est le numéro #{handles.index {|x| x == "@epenser"}}"
end


# #Répartition par taille des handles
#1 trouver le plus grand handle
#2 créer une boucle selon le plus grand handle : tant que x n'a pas atteint le plus grand handle, répéter

def max_handles(handles)
    puts "la plus grande valeur de cette liste est #{handles.max {|a, b| a.length <=> b.length }}, elle va nous permettre de créer une boucle qui tend vers cette valeur"

    nbcara = 0

    while nbcara <= 35
        puts "Il y a dans cette liste #{handles.count {|x| x.length == nbcara}} handle d'une taille de #{nbcara} caractère"
        nbcara += 1
    end
end


